import React from 'react';
import { Nav, NavItem, NavLink, FormGroup, Input, Button, Label, Col} from 'reactstrap';
import { Link} from "react-router-dom";

export default class SideNav extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isOpen: false,
      inputValue: '',
    };
    this.sendSearch = this.sendSearch.bind(this);
    this.updateInputValue = this.updateInputValue.bind(this);
  }
  updateInputValue(evt) {
    this.setState({
      inputValue: evt.target.value
    })}
  sendSearch(){

  }
  render() {
    return (
      <div>


        <Nav vertical>
          <NavItem fluid>
            <FormGroup>
            <Label for="search" style={{fontWeight: 'bold', marginBottom: '0'}}>ID</Label>
            <div>
            <div style={{textAlign: "center", display: 'inline-block'}}>
              <Input name="search" id="search" value={this.state.inputValue} onChange={evt => this.updateInputValue(evt)} style={{width: "100%"}}></Input>
            </div>
            </div>
            <Label for="episodes" style={{fontWeight: 'bold', marginBottom: '0'}}>Series</Label>
            <div>
            <div style={{textAlign: "center", display: 'inline-block'}}>
              <Input name="episode" id="episode" value={this.state.inputValue} onChange={evt => this.updateInputValue(evt)} style={{width: "100%"}}></Input>
            </div>
            </div>
            <Label for="search" style={{fontWeight: 'bold', marginBottom: '0'}}>Season</Label>
            <div>
            <div style={{textAlign: "center", display: 'inline-block'}}>
              <Input name="search" id="search" value={this.state.inputValue} onChange={evt => this.updateInputValue(evt)} style={{width: "100%"}}></Input>
            </div>
            </div>
            <Label for="search" style={{fontWeight: 'bold', marginBottom: '0'}}>Episode</Label>
            <div>
            <div style={{textAlign: "center", display: 'inline-block'}}>
              <Input name="search" id="search" value={this.state.inputValue} onChange={evt => this.updateInputValue(evt)} style={{width: "100%"}}></Input>
            </div>
            </div>
            <Link to="/searchResults"><Button onClick={this.sendSearch} style={{marginTop: "5px"}}>Search</Button></Link>
            </FormGroup>
          </NavItem>
        </Nav>
      </div>

    );
  }
}
